final List<dynamic> styles = [':host {\n  background-color: var(--primary);\n  color: #fff;\n  font-weight: 800;\n  border: none;\n  display: inline-block;\n  padding: 15.4px 34px;\n  border-radius: 6px;\n  font-size: 16px;\n}\n:host:focus {\n  outline: none;\n}\n:host:hover {\n  background-color: var(--primary-dark);\n}\n:host:active {\n  background-color: var(--primary-darker);\n}\n:host:disabled {\n  background-color: #abe5eb;\n  color: rgba(255, 255, 255, 0.5);\n}\n:host.fluidBtn-small {\n  height: 40px;\n  padding: 8px 34px !important;\n}\n:host.fluidBtn-secondary {\n  color: var(--primary);\n  background: #f8f8fc;\n}\n:host.fluidBtn-secondary:hover {\n  background: #f3f3f7;\n}\n:host.fluidBtn-secondary:active {\n  background: #e9e9ed;\n}\n:host.fluidBtn-highlight {\n  color: #000000;\n  background: var(--accent);\n}\n:host.fluidBtn-highlight:hover {\n  background: var(--accent-dark);\n}\n:host.fluidBtn-highlight:active {\n  background: var(--accent-darker);\n}\n:host.fluidBtn-ghost {\n  color: var(--primary);\n  background-color: transparent;\n  font-weight: bold;\n}\n:host.fluidBtn-ghost:hover {\n  color: var(--primary-dark);\n}\n:host.fluidBtn-ghost:active {\n  color: var(--primary-dark);\n}\n'];