final List<dynamic> styles = ['.container {\n  width: 100%;\n  border-radius: 6px;\n  position: relative;\n  background-color: var(--inputBackground);\n  color: #1b1b25;\n  font-size: 14px;\n  cursor: pointer;\n}\n.container:hover {\n  box-shadow: 0px 5px 10px rgba(0, 0, 0, 0.05);\n}\n.container.opened {\n  border-radius: 6px 6px 0 0;\n  background-color: var(--dropdown-background);\n}\n\n.dp_c {\n  padding: 10px 16px;\n  display: flex;\n  justify-content: space-between;\n  align-content: center;\n  flex-direction: row;\n}\n.dp_c fluid-icon {\n  color: var(--primary);\n}\n.dp_c.selected {\n  color: var(--primary);\n}\n.dp_c.disabled {\n  color: var(--disabledColor);\n}\n.dp_c.disabled fluid-icon {\n  color: var(--disabledColor);\n}\n\n.dp_item {\n  padding: 10px 16px;\n  border-radius: 0 0 6px 6px;\n  border-top: 1px solid var(--dropdown-hover);\n}\n.dp_item:hover {\n  background-color: var(--dropdown-hover);\n}\n.dp_item.active, .dp_item:active {\n  color: var(--primary);\n  background-color: var(--dropdown-hover);\n}\n\n.menu {\n  width: 100%;\n  position: absolute;\n  z-index: 2;\n  background-color: var(--dropdown-background);\n  border-radius: 0 0 6px 6px;\n  box-shadow: 0px 5px 10px rgba(0, 0, 0, 0.05);\n}\n'];