final List<dynamic> styles = [':host {\n  /* modals are hidden by default */\n  display: none;\n}\n:host.fluidModal-open {\n  /* body overflow is hidden to hide main scrollbar when modal window is open */\n  display: block;\n  overflow: hidden;\n}\n:host .modal {\n  position: fixed;\n  top: 50vh;\n  left: 50vw;\n  background: #fff;\n  transform: translate(-50%, -50%);\n  z-index: 1000;\n  overflow: auto;\n  max-height: 80vh;\n  min-width: 650px;\n  box-shadow: 0px 30px 40px rgba(0, 0, 0, 0.2), 0px 2px 4px rgba(0, 0, 0, 0.05);\n  border-radius: 6px;\n}\n:host .modal-header {\n  display: flex;\n  flex-direction: row;\n  justify-content: space-between;\n  padding: 16px;\n  font-size: 12pt;\n  background: #f8f8fc;\n  margin-bottom: -1px;\n  /* margin exposes part of the modal background */\n}\n:host .modal-header fluid-icon {\n  color: var(--primary);\n  cursor: pointer;\n}\n:host .modal-header fluid-icon:hover {\n  color: var(--primary-darker);\n}\n:host .modal-body {\n  padding: 40px;\n  height: 100%;\n  /* margin exposes part of the modal background */\n}\n:host .modal-background {\n  /* modal background fixed across whole screen */\n  position: fixed;\n  top: 0;\n  right: 0;\n  bottom: 0;\n  left: 0;\n  /* semi-transparent black  */\n  background-color: #000;\n  opacity: 0.45;\n  /* z-index must be below .jw-modal and above everything else  */\n  z-index: 900;\n}\n'];