final List<dynamic> styles = ['div {\n  width: 80px;\n  background-color: #F3F3F7;\n  height: 100%;\n}\ndiv.fixed {\n  position: fixed;\n  left: 0;\n  bottom: 0;\n  background-color: red;\n}\ndiv.expanded {\n  width: 200px;\n}\ndiv.expand {\n  animation-duration: 50ms;\n  animation-name: expand;\n}\ndiv.shrink {\n  animation-duration: 50ms;\n  animation-name: shrink;\n}\n\n.container {\n  width: 100%;\n  height: 100%;\n  display: flex;\n  flex-direction: column;\n  align-items: flex-start;\n  justify-content: space-between;\n}\n.container .expand {\n  box-sizing: border-box;\n  height: 80px;\n  font-size: 25px;\n  padding: 25px;\n}\n.container .expand :hover {\n  color: var(--primary-dark);\n}\n\n.scrollable {\n  width: 100%;\n  overflow: auto;\n}\n\n@keyframes expand {\n  from {\n    width: 80px;\n  }\n  to {\n    width: 200px;\n  }\n}\n@keyframes shrink {\n  from {\n    width: 200px;\n  }\n  to {\n    width: 80px;\n  }\n}\n'];