final List<dynamic> styles = ['.grid:not(.scrollable) {\n  display: grid;\n  grid-template-columns: 1fr 1fr 1fr 1fr;\n  grid-column-gap: 24px;\n  grid-row-gap: 24px;\n}\n\n.scrollable {\n  display: flex;\n  flex-direction: row;\n  overflow-x: auto;\n  justify-content: center;\n}\n.scrollable doc-preview {\n  padding: 0 10px;\n  width: 75%;\n  min-width: 250px;\n}\n\n@media screen and (max-width: 960px) {\n  .grid:not(.scrollable) {\n    grid-template-columns: 1fr 1fr;\n  }\n}\n@media screen and (max-width: 480px) {\n  .grid:not(.scrollable) {\n    grid-template-columns: 1fr;\n  }\n}\n'];