final List<dynamic> styles = ['fluid-card {\n  height: 100% !important;\n  cursor: pointer;\n}\nfluid-card:hover {\n  box-shadow: 0px 30px 40px rgba(0, 0, 0, 0.2), 0px 2px 4px rgba(0, 0, 0, 0.05);\n}\nfluid-card:hover h3 {\n  text-decoration: underline;\n  text-underline-position: under;\n}\n\nh3 {\n  font-size: 14pt;\n  text-align: center;\n  margin: 8px 0;\n  white-space: nowrap;\n  overflow: hidden;\n  text-overflow: ellipsis;\n}\n\np {\n  display: block;\n  /* Fallback for non-webkit */\n  display: -webkit-box;\n  font-size: 12pt;\n  line-height: 120%;\n  height: 60px;\n  overflow: hidden;\n  -webkit-line-clamp: 3;\n  -webkit-box-orient: vertical;\n  text-overflow: ellipsis;\n}\n\n.img-container {\n  overflow: hidden;\n  height: 0;\n  padding-top: 56.25%;\n  position: relative;\n  margin: -4%;\n  margin-bottom: 4px;\n}\n\nimg {\n  position: absolute;\n  top: 0;\n  width: 100%;\n  height: 100%;\n  border-radius: 5px;\n  object-fit: cover;\n}\n'];