final List<dynamic> styles = ['fluid-spinner {\n  position: absolute;\n  top: 20%;\n  left: 50%;\n}\n\n.container {\n  min-height: 100vh;\n  max-width: 860px;\n  margin: auto;\n  padding: 32px 16px;\n}\n\n.content {\n  padding: 4px 20px;\n}\n\n.date {\n  text-align: end;\n}\n\n.title {\n  font-family: Lato;\n  font-style: normal;\n  font-weight: 900;\n  font-size: 32px;\n  color: var(--primary);\n  margin: 4px 0;\n}\n\n.img-container {\n  overflow: hidden;\n  height: 0;\n  padding-top: 30%;\n  position: relative;\n  background-color: var(--primary-dark);\n  border-radius: 10px;\n}\n.img-container img {\n  position: absolute;\n  top: 0;\n  width: 100%;\n  height: 100%;\n  object-fit: cover;\n  object-position: center;\n}\n.img-container .startVideo {\n  position: absolute;\n  top: 50%;\n  left: 50%;\n  width: 60px;\n  height: 60px;\n  border-radius: 30px;\n  background: rgba(58, 58, 58, 0.2);\n  transform: translate(-50%, -50%);\n  cursor: pointer;\n}\n.img-container .startVideo fluid-icon {\n  position: absolute;\n  color: rgba(255, 255, 255, 0.7);\n  top: 10px;\n  left: 12px;\n}\n.img-container .startVideo:hover {\n  background: rgba(58, 58, 58, 0.6);\n}\n.img-container .startVideo:hover fluid-icon {\n  color: white;\n}\n.img-container .startVideo:active {\n  background: #3a3a3a;\n}\n.img-container .video {\n  position: absolute;\n  top: 0;\n  width: 60%;\n  height: 100%;\n  padding: 0 20%;\n}\n.img-container .video iframe {\n  width: 100%;\n  height: 100%;\n  background-color: black;\n}\n.img-container .video .stopVideo {\n  position: absolute;\n  top: calc(50% - 20px);\n  right: calc(20% - 20px);\n  width: 40px;\n  height: 40px;\n  border-radius: 20px;\n  background: rgba(58, 58, 58, 0.4);\n  cursor: pointer;\n}\n.img-container .video .stopVideo fluid-icon {\n  position: absolute;\n  color: rgba(255, 255, 255, 0.7);\n  top: 10px;\n  left: 12px;\n}\n.img-container .video .stopVideo:hover {\n  background: rgba(58, 58, 58, 0.6);\n}\n.img-container .video .stopVideo:hover fluid-icon {\n  color: white;\n}\n.img-container .video .stopVideo:active {\n  background: #3a3a3a;\n}\n'];