final List<dynamic> styles = ['.container {\n  min-height: 100vh;\n  padding: 2% 30%;\n}\n\nimg {\n  width: 100%;\n  border-radius: 10px;\n}\n\n.inputs {\n  margin: 48px 16px;\n  --inputBackground: #e9e9ed;\n}\n.inputs .error {\n  color: var(--errorColor);\n}\n.inputs .success {\n  color: var(--primary);\n}\n.inputs input,\n.inputs textarea {\n  margin: 8px 0;\n  width: calc(100% - 16px);\n}\n.inputs button {\n  width: 200px;\n  margin: 16px calc(50% - 108px);\n}\n\n@media screen and (max-width: 960px) {\n  .container {\n    padding: 2% 16px;\n  }\n}\n'];