final List<dynamic> styles = ['@font-face {\n  font-family: "Minecraft";\n  font-style: normal;\n  font-weight: 400;\n  src: url("fonts/minecraft_font.woff") format("woff");\n}\n.container {\n  position: relative;\n}\n\n.logo {\n  width: 20vw;\n  margin: 2% calc(40vw - 2.25rem);\n}\n\n.floatingCard {\n  margin: 8% 6%;\n  width: 45%;\n}\n\nfluid-card {\n  margin: 8% 6%;\n  width: 45%;\n}\n\n.video {\n  width: 35vw;\n  position: absolute;\n  right: 5%;\n  background: black;\n  height: 19.69vw;\n  margin-top: -24vw;\n}\n.video iframe {\n  height: 100%;\n  width: 100%;\n}\n\n.grid {\n  margin: 5vw;\n  display: grid;\n  grid-template-columns: 1fr 1fr 1fr 1fr;\n  grid-gap: 1vw;\n}\n\n.item {\n  min-height: 200px;\n}\n.item h5 {\n  font-family: Minecraft;\n  text-align: center;\n  border-bottom: 1px solid #373737;\n  padding: 2% 0;\n  margin: 0 4%;\n  font-weight: 500;\n  font-size: 1.25rem;\n}\n.item .icon {\n  image-rendering: pixelated;\n  width: 30%;\n  margin: 4% 35%;\n}\n.item:hover {\n  border: 2px solid #373737;\n  border-right-color: #fff;\n  border-bottom-color: #fff;\n  background: #c6c6c6;\n  border-radius: 5px;\n  transform: scale(1.1);\n}\n.item:hover .crafting-grid {\n  display: none;\n}\n.item:hover .description {\n  line-height: 2rem;\n  height: 12vw;\n  visibility: visible;\n  margin: 5% 8%;\n}\n\n.description {\n  font-family: Minecraft;\n  visibility: hidden;\n  height: 5px;\n  font-size: 14px;\n  color: #555555;\n}\n\n.crafting-grid {\n  padding: 3% 6%;\n  background: #c6c6c6;\n  width: calc(12vw + 12%);\n  margin: 2% auto;\n  display: grid;\n  grid-template-columns: 1fr 1fr 1fr;\n}\n\n.crafting-blue {\n  background: #4a6e9e;\n}\n.crafting-blue .crafting-item {\n  background-color: #344e70;\n  border: 3px solid #1c2a3d;\n  border-right-color: #6ea4ea;\n  border-bottom-color: #6ea4ea;\n}\n\n.crafting-item {\n  height: 4vw;\n  background-color: #8b8b8b;\n  border: 3px solid #373737;\n  border-right-color: #fff;\n  border-bottom-color: #fff;\n}\n.crafting-item img {\n  image-rendering: pixelated;\n  height: 100%;\n  width: 100%;\n}\n'];