final List<dynamic> styles = ['#card .split {\n  display: grid;\n  grid-template-columns: 1fr 1fr;\n}\n\n.result {\n  position: absolute;\n  right: 5%;\n  margin-top: -37%;\n  width: calc(100% / 6);\n  border: 8px solid #505050;\n  text-align: center;\n}\n\n#convertIcon {\n  position: absolute;\n  right: 35%;\n  margin-top: -32%;\n  height: 50px;\n  text-align: center;\n  font-size: 50px;\n}\n\n.centered {\n  position: absolute;\n  width: 100%;\n  text-align: center;\n  top: 50%;\n  transform: translateY(-50%);\n}\n\n.output button {\n  position: absolute;\n  top: 16px;\n  right: 16px;\n  display: none;\n}\n.output textarea {\n  width: 90%;\n  height: 90%;\n  resize: none;\n}\n.output:hover button {\n  display: block;\n}\n'];